package source.`1690`.`石子游戏 VII`

import kotlin.math.max

class SolutionByDP {
    fun stoneGameVII(stones: IntArray): Int {
        val n = stones.size
        val sums = IntArray(n + 1) { 0 } // 前缀和数组
        // dp[i][j] 表示从 i 到 j 的子区间中，先手能拉开的最大差距
        val dp = Array(stones.size) { IntArray(stones.size) { 0 } }

        repeat(sums.size) {
            if (it > 0) {
                sums[it] = sums[it - 1] + stones[it - 1]
            } else {
                sums[0] = 0
            }
        }
        for (i in n - 2 downTo 0) {
            for (j in i + 1 until n) {
                dp[i][j] = max(sums[j + 1] - sums[i + 1] - dp[i + 1][j], sums[j] - sums[i] - dp[i][j - 1])
            }
        }
        return dp[0][n - 1]
    }
}

